import React, { Component } from 'react';
import Cards from 'components/cards/cards.jsx'
import LoadingComponent from 'components/loading/loading.jsx';
import API from 'helpers/api.js';
import axios from 'axios';

class CourseHome extends Component {
    constructor(props) {
        super(props);
       
        this.state = {
            catalogue:[],
            
            agents:[{
                "id": 1,
                "Agent":"Osama Ahmed Tahir"
            },{
             
                "id": 2,
                "Agent":"Ali Jasim"
            },{
                
                "id": 3,
                "Agent":"MukhtaBani"

            },{
                "id": 4,
                "Agent":"Sanchit"

            }
            ,{
                "id": 5,
                "Agent":"Ali"
            }]

        }
    }
    componentDidMount() {
        this.getCourses();
      }
    
      stateHandler = (state) => {
        this.setState(state);
      }
    
      getCourses = () => {
        API.getCourses(this.stateHandler);
      }
    
  render() {
    
    if (this.state.catalogue.length == 0) return <LoadingComponent/>;
    console.log(this.state.catalogue)
    console.log(this.state.catalogue[0].Title)
    return (
      <div className="Courses">
      <div className="container">
   
      <div>     
           {
            this.state.catalogue.map((value,id) => {
                        return (
                            <di>
                                <h1>{this.state.catalogue.Title}</h1>
                                <Cards key={id} showButton="true" data={value} agents={this.state.agents}/>
                                </di>
                        )

                    })
                }
            </div>
            </div>
      </div>
    );
  }
}

export default CourseHome;
