import React, { Component } from "react";
import M from 'materialize-css';

class Sidebar extends Component {
    constructor(props) {
        super(props);
        this.state = {
           universty:'Deakin',
           experience:'5',
           fees:'500000',
           country:'USA',
           coursetype:'Master',
           updatedexperience:''
        }
        this.updateExperience=this.updateExperience.bind(this)
        this.handleExperienceChange=this.handleExperienceChange.bind(this)
        this.updateUniversity=this.updateUniversity.bind(this)
        this.handleUniversirtyChange=this.handleUniversirtyChange.bind(this)
       
    }
    
    
       
    

    componentDidMount() {
        var elem = document.querySelector(".sidenav");
        var instance = M.Sidenav.init(elem, {
            edge: "left",
            inDuration: 250
        });
        var elems = document.querySelectorAll('select');
        var instances = M.FormSelect.init(elems);
        
    }
    
    updateUniversity = (event) => {
        this.setState({universty:event.target.value});
        
    }
    updateExperience = (event) => {
        this.setState({experience:event.target.value});
    }
    componentDidUpdate(prevProps, prevState) {
        var Experience=1;
        var University=1;
        var Fees=1;
        var Country=1;
        var CourseType=1;
        if (prevState.experience!=this.state.experience) {
            this.setState ({experience:this.state.experience},this.handleExperienceChange(Experience))
        }
        
        if(prevState.universty!=this.state.universty){
            this.setState({universty:this.state.universty},this.handleUniversirtyChange(University))
        }
    }
      
    handleExperienceChange = (Experience) => {
        if(Experience===1)
        {
           var Experience=this.state.experience;
           this.props.dataexperience(Experience)  
        }        
    }
    handleUniversirtyChange = (University) => {
        if(University===1)
        {
          var University=this.state.universty;
          this.props.datauniversity(University)  
        }        
    }
    
    updateFees = (event) => {
        this.setState({fees:event.target.value});
    }
    updateCountry = (event) => {
        this.setState({country:event.target.value});
    }
    updateCourseType = (event) => {
        this.setState({coursetype:event.target.value});
    }
   
    onChangedExperience = (event) =>{
        this.handleExperienceChange();
        this.updateExperience(event);
        
    }
    onChangedUniversity = (event) =>{
        this.handleUniversirtyChange();
        this.updateUniversity(event);
        
    }
    
    render() {
        
      
        return (
            <div>
                <ul id="slide-out" className="sidenav">
                <h6><b>Filteration Center</b></h6>
                <br></br>
                <h7><b>Select University</b></h7> 
                <div className="input-field col s12">
                <select id="University" onChange={this.updateUniversity}>
                    <optgroup label="University">
                        <option value="Deakin">Deakin</option>
                        <option value="RMIT">RMIT</option>
                    </optgroup>
                    </select>
                </div>
                <h7><b>Number of experience</b></h7> 
                <div className="input-field col s12">
                <select id="Experience" onChange={this.onChangedExperience}>
                    <optgroup label="Experience">
                        <option value="5">5</option>
                        <option value="6">6</option>
                    </optgroup>
                    </select>
                </div>
                <h7><b>Fees</b></h7> 
                <div className="input-field col s12">
                <select  id="Fees" onChange={this.updateFees}>
                    <optgroup label="Fees">
                        <option value="500000">500000</option>
                        <option value="600000">600000</option>
                    </optgroup>
                    </select>
                </div>
                <h7><b>Country</b></h7>
                <div className="input-field col s12">
                <select id="Country" onChange={this.updateCountry}>
                    <optgroup label="Country">
                        <option value="USA">USA</option>
                        <option value="Pakistan">Pakistan</option>
                    </optgroup>
                    </select>
                </div>
                <h7><b>Course Type</b></h7>
                <div class="input-field col s12">
                <select id="CourseType" onChange={this.updateCourseType}>
                    <optgroup label="Course Type">
                        <option value="Master">Master</option>
                        <option value="PHD">PHD</option>
                        <option value="Bachelor">Bachelor</option>
                    </optgroup>
                    </select>
                </div>
                </ul>
                <a href="#" data-target="slide-out" className="sidenav-trigger">
                          <i className="medium material-icons">search</i>
                </a>
            </div>
        );
    }
}

export default Sidebar;
