import React, { Component } from 'react';
import Cards from 'components/cards/cards.jsx'
import LoadingComponent from 'components/loading/loading.jsx';
import API from 'helpers/api.js';


class CourseHome extends Component {
    constructor(props) {
        super(props);
       
        this.state = {
            catalogue:[],
            agents:[],
            search:''
           

        }
    }
    updateSearch(event){
      console.log(event.target.value);
    }
    componentDidMount() {
        this.getCourses();
        this.getAgents();
      }
    
      stateHandler = (state) => {
        this.setState(state);
      }
    
      getCourses = () => {
        API.getCourses(this.stateHandler);
      }
      getAgents = () => {
        API.getAgents(this.stateHandler);
      }
    
  render() {
    
    if (this.state.catalogue.length == 0) return <LoadingComponent/>;
    if (this.state.agents.length == 0) return <LoadingComponent/>;
    let filteredCourses=this.state.catalogue.filter(
      (catalogue)=>{
        return catalogue.Title.indexOf(this.state.search) != -1;
      }
  );
    return (
      <div className="Courses">
      <div className="container">
      <input type="text" value={this.state.search} onChange={this.updateSearch.bind(this)} />
      <div>     
           {
            filteredCourses.map((value,id) => {
                        return (
                            <div>
                                <Cards key={id} showButton="true" data={value} agents={this.state.agents}/>
                                </div>
                        )

                    })
                }
            </div>
            </div>
      </div>
    );
  }
}

export default CourseHome;
